package demo_9;

public class FoorLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// If you exactly know the start and end point 
		
		
		
		for(int i = 1; i <= 10; i++) {
			
			if (i==7) {
				break;   // continue
			}
			
			System.out.println("value of my loop : " + i);
			
		}

	}

}
